contracts/Challenge4.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./INFTFlags.sol";

contract Challenge4 is Ownable {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);

    address public nftContract;
    mapping(address => bool) public isMinter;

    constructor(address _nftContract) Ownable(msg.sender) {
        nftContract = _nftContract;
    }

    function addMinter(address _minter) public onlyOwner {
        isMinter[_minter] = true;

        emit MinterAdded(_minter);
    }

    function removeMinter(address _minter) public onlyOwner {
        isMinter[_minter] = false;

        emit MinterRemoved(_minter);
    }

    function mintFlag(address _minter, bytes memory signature) public {
        require(isMinter[_minter], "Not a minter");

        bytes32 message = keccak256(abi.encode("BG CTF Challenge 4", msg.sender));
        bytes32 hash = message.toEthSignedMessageHash();

        address recoveredSigner = hash.recover(signature);

        require(recoveredSigner == _minter, "Invalid signature");

        INFTFlags(nftContract).mint(msg.sender, 4);
    }
}

contracts/Challenge5.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./INFTFlags.sol";

contract Challenge5 {
    address public nftContract;
    mapping(address => uint256) public points;
    uint256 public constant POINTS_TO_MINT = 10;

    constructor(address _nftContract) {
        nftContract = _nftContract;
    }

    function resetPoints() public {
        points[tx.origin] = 0;
    }

    function claimPoints() public {
        require(points[tx.origin] == 0, "Already claimed points");
        (bool success, ) = msg.sender.call("");
        require(success, "External call failed");

        points[tx.origin] += 1;
    }

    function mintFlag() public {
        require(points[tx.origin] >= POINTS_TO_MINT, "Not enough points");
        points[tx.origin] -= POINTS_TO_MINT;
        INFTFlags(nftContract).mint(tx.origin, 5);
    }
}

contracts/Challenge6.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/Strings.sol";

import "./INFTFlags.sol";

interface IContract6Solution {
    function name() external view returns (string memory);
}

contract Challenge6 {
    using Strings for uint256;

    address public nftContract;
    uint256 public count;

    constructor(address _nftContract) {
        nftContract = _nftContract;
    }

    function mintFlag(uint256 code) public {
        require(code == count << 8, "Wrong code");
        require(
            keccak256(abi.encodePacked(IContract6Solution(msg.sender).name())) ==
                keccak256("BG CTF Challenge 6 Solution"),
            "Wrong name"
        );
        uint256 gas = gasleft();
        require(gas > 190_000 && gas < 200_000, string.concat("Wrong gas: ", gas.toString()));

        INFTFlags(nftContract).mint(tx.origin, 6);
        count += 1;
    }
}

contracts/Challenge7.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./INFTFlags.sol";

contract Challenge7Delegate {
    address public owner;
    event OwnerChange(address indexed owner);

    constructor(address _owner) {
        owner = _owner;
        emit OwnerChange(_owner);
    }

    function claimOwnership() public {
        owner = msg.sender;
        emit OwnerChange(msg.sender);
    }
}

contract Challenge7 {
    address public owner;
    Challenge7Delegate delegate;
    address public nftContract;

    constructor(address _nftContract, address _delegateAddress, address _owner) {
        nftContract = _nftContract;
        delegate = Challenge7Delegate(_delegateAddress);
        owner = _owner;
    }

    function mintFlag() public {
        require(msg.sender == owner, "Only owner");
        INFTFlags(nftContract).mint(msg.sender, 7);
    }

    fallback() external {
        (bool result, ) = address(delegate).delegatecall(msg.data);
        if (result) {
            this;
        }
    }
}

